import './App.css';
import './bounce.css';
import React, {Component} from 'react';
import Typewriter from "typewriter-effect";
import $ from 'jquery'
import SportsEsportsIcon from '@mui/icons-material/SportsEsports';
import SkateboardingIcon from '@mui/icons-material/Skateboarding';
import LiveTvIcon from '@mui/icons-material/LiveTv';
function App() {

return (
<>
  <div id='intro'>
    <div id='name'>
      <div className="bouncing-text">
        <div className="b">h</div>
        <div className="o">i</div>
        <div className="u">,</div>
      </div>
      <div className="bouncing-text">
        <div className="b">i</div>
        <div className="o">'</div>
        <div className="u">m</div>
        <div className="n"></div>
        <div className="c">N</div>
        <div className="e">a</div>
        <div className="f">v</div>
        <div className="g">i</div>
        <div className="h">d</div>
      </div>
    </div>
  </div>

  <div  id='aboutMe'>
    <div id='background'>
      <div id='content'>Me</div>
    </div>
    <div id='type'>
    <Typewriter 
    onInit={(typewriter)=> {
    typewriter
    .typeString("Who Am I?")
    .start();
    }
    }
    />
    </div>
    <div id='text'>
      <h3>I'm Navid, an undergraduate Computer Science & Web Development student at NYU. My hobbies include staying up late playing games, watching shows and anime, and skateboarding.</h3>
    </div>
    <div id='pics'>
      <div id='controller'>
        <SportsEsportsIcon/>
      </div>
      <div id='skateboard'>
        <SkateboardingIcon/>
      </div>
      <div id='tv'>
        <LiveTvIcon/>
      </div>
    </div>
  </div>

  <div  id='skills'>
  <div id='background'>
    <div id='content2'>Skills</div>
    </div>
    <Typewriter
    onInit={(typewriter)=> {
    typewriter
    .typeString("Some Things I Know")
    .start();
    }
    }
    />
    <div id='skillsList'>

    </div>
  </div>

  <div id='contactMe'>
    <Typewriter
    onInit={(typewriter)=> {
    typewriter
    .typeString("Contact Me")
    .start();
    }
    }
    />
  </div>
</>
  
  );
  
}

