import './App.css';
import './bounce.css';
import './contact.css'

import React, {Component, useEffect} from 'react';
import Typewriter from "typewriter-effect";
import $ from 'jquery'
import SportsEsportsIcon from '@mui/icons-material/SportsEsports';
import SkateboardingIcon from '@mui/icons-material/Skateboarding';
import LiveTvIcon from '@mui/icons-material/LiveTv';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import Button from '@mui/material/Button';
import { TextField } from '@material-ui/core';
import Aos from "aos";
import "aos/dist/aos.css";
<script src='https://smtpjs.com/v3/smtp.js'></script>
function App() {
  useEffect(() =>{
    Aos.init(); 
  }, [])
 
return (
<>
<link rel="stylesheet" href="https://unpkg.com/aos@next/dist/aos.css" />
  <div id='intro'>
    <div id='name'>
      <div className="bouncing-text">
        <div className="b">h</div>
        <div className="o">i</div>
        <div className="u">,</div>
      </div>
      <div className="bouncing-text">
        <div className="b">i</div>
        <div className="o">'</div>
        <div className="u">m</div>
        <div className="n"></div>
        <div className="c">N</div>
        <div className="e">a</div>
        <div className="f">v</div>
        <div className="g">i</div>
        <div className="h">d</div>
      </div>
    </div>
  </div>


  <div id='aboutMe' >
    <div id='background'>
      <div id='content'>Me</div>
    </div>
    <div id='type'>
    <Typewriter 
    onInit={(typewriter)=> {
    typewriter
    .typeString("Who Am I?")
    .start();
    }
    }
    />
    </div>
    <div id='thing'>
