import './App.css';
import './bounce.css';
import React, { useRef, useEffect, useCallback }from 'react';
import Typewriter from "typewriter-effect";
import $ from 'jquery'
function App() {
  const ref = useRef();

  // The scroll listener
  const handleScroll = useCallback(() => {
    console.log("scrolling");
  }, []);

  // Attach the scroll listener to the div
  useEffect(() => {
    const div = ref.current;
    console.log("Div is ", div);
    // Attach a scroll listener to the div
    div.addEventListener("scroll", handleScroll);
  }, [handleScroll]);

  return (
  <>

  <div id='intro'>
    <div id='name'>
      <div className="bouncing-text">
        <div className="b">h</div>
        <div className="o">i</div>
        <div className="u">,</div>
      </div>
      <div className="bouncing-text">
        <div className="b">i</div>
        <div className="o">'</div>
        <div className="u">m</div>
        <div className="n">N</div>
        <div className="c">a</div>
        <div className="e">v</div>
        <div className="f">i</div>
        <div className="g">d</div>
      </div>
    </div>
  </div>
 
  
  <div id='aboutMe'>
    
      {/* <Typewriter
      onInit={(typewriter)=> {
      typewriter
      .typeString("About Me")
      .start();
      }}
      /> */}
    
  </div>
  </>
  );
  
}




export default App;
//viewport height + 50% viewport height

$(function(){
  $(window).scroll(function(){
    var aTop = $('#aboutMe').height();
    if($(this).scrollTop()>=aTop){
   
    }
  });
});
