import './App.css';
import './bounce.css';
import './slideIn.css';
import React, {Component} from 'react';
import Typewriter from "typewriter-effect";
import $ from 'jquery'
import SportsEsportsIcon from '@mui/icons-material/SportsEsports';
import SkateboardingIcon from '@mui/icons-material/Skateboarding';
import LiveTvIcon from '@mui/icons-material/LiveTv';
function App() {

 
return (
<>
<link rel="stylesheet" href="https://unpkg.com/aos@next/dist/aos.css" />
  <div id='intro'>
    <div id='name'>
      <div className="bouncing-text">
        <div className="b">h</div>
        <div className="o">i</div>
        <div className="u">,</div>
      </div>
      <div className="bouncing-text">
        <div className="b">i</div>
        <div className="o">'</div>
        <div className="u">m</div>
        <div className="n"></div>
        <div className="c">N</div>
        <div className="e">a</div>
        <div className="f">v</div>
        <div className="g">i</div>
        <div className="h">d</div>
      </div>
    </div>
  </div>


  <div id='aboutMe' >
    <div id='background'>
      <div id='content'>Me</div>
    </div>
    <div id='type'>
    <Typewriter 
    onInit={(typewriter)=> {
    typewriter
    .typeString("Who Am I?")
    .start();
    }
    }
    />
    </div>
    <div id='text'>
      <h3>I'm Navid, an undergraduate Computer Science & Web Development student at NYU. My hobbies include staying up late playing games, watching shows and anime, and skateboarding.</h3>
    </div>
    <div id='pics'>
      <div id='controller'>
        <SportsEsportsIcon style={{ fontSize: 150 }} />
      </div>
      <div id='skateboard'>
        <SkateboardingIcon style={{ fontSize: 150 }} />
      </div>
      <div id='tv'>
        <LiveTvIcon style={{ fontSize: 150 }}/>
      </div>
    </div>
  </div>


  <div  id='skills'>
  <div id='background'>
    <div id='content2'>Skills</div>
    </div>
    <div id='type'>
    <Typewriter
    onInit={(typewriter)=> {
    typewriter
    .typeString("Some Things I Know")
    .start();
    }
    }
    />
    </div>
    <div id='skillsList'>
      <div id='coding'>
        <div>Python</div>
        <div>Java</div>
        <div>Javascript</div>
        <div>NodeJS</div>
        <div>React</div>
        <div>HTML</div>
        <div>CSS</div>
        <div>Swift</div>
        <div>LatEx</div>
      </div>
      <div id='cloud'>
        <div>Amazon Web Services</div>
        <div>DynamoDB</div>
        <div>Lambda</div>
        <div>Cloudwatch</div>
        <div>S3</div>
        <div>API Gateway</div>
        <div>AWS Amplify</div>
        <div>EC2</div>
        <div>ECR</div>
        <div>ECS</div>
      </div>
      <div id='other'>
        <div>Squarespace</div>
        <div>Xcode</div>
        <div>Overleaf</div>
        <div>Autodesk Inventor</div>
        <div>Adobe Photoshop</div>
        <div>Adobe Illustrator</div>
        <div>Adobe InDesign</div>
      </div>
    </div>
  </div>

  <div id='contactMe' className="animated fade-out">
    <div id='type'>
      navid
    {/* <Typewriter
    onInit={(typewriter)=> {
    typewriter
    .typeString("Contact Me")
    .start();
    }
    }
    /> */}
    </div>
  </div>
  
</>
  
  );

}

